import React, {useState, useEffect} from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity } from "react-native";
import {modificaFoto1, modificaFoto2, modificaFoto3, modificaFoto4, modificaFoto5} from '../actions/ProblemaActions';
import { Header } from 'react-native-elements';
import { Actions } from 'react-native-router-flux';
import { MaterialIcons } from '@expo/vector-icons';
import { Camera } from 'expo-camera';
import api from '../../services/api';


import { connect } from 'react-redux';
import AwesomeAlert from 'react-native-awesome-alerts';
import * as ImagePicker from 'expo-image-picker';


export const FormCadastroProblema = (props) => {
  const [Imagem, setImagem] = useState([], []);
  const [nomeImagem, setnomeImagem] = useState([], []);
  const [Counter, setCounter] = useState("1");

  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }

  async function snap(){

    setCounter(
      Counter +1
    )
    if(Counter >= 111111 ){
      Actions.enviarProblema();
    }

    else if (Counter < 111111) {
      let photo = await this.camera.takePictureAsync()
      let uri = photo.uri;

      if(Counter == 1)
      props.modificaFoto1(uri);

      else if(Counter == 11)
        props.modificaFoto2(uri);

      else if(Counter == 111)
        props.modificaFoto3(uri);

      else if(Counter == 1111)
        props.modificaFoto4(uri);

      else if(Counter == 11111)
        props.modificaFoto5(uri);


      setImagem(
        [...Imagem,uri]
     )

    }

  }




  async function openImagePickerAsync() {


    const permissionResult = await ImagePicker.requestCameraRollPermissionsAsync();

    if (permissionResult.granted === false) {
      alert("Permission to access camera roll is required!");
      return;
    }
    if(permissionResult.granted) {

    setCounter(
      Counter +1
    )

    if(Counter >= 111111 ){
      Actions.enviarProblema();
    }
    else if(Counter < 111111){
      const  retorno  = await ImagePicker.launchImageLibraryAsync();
      const uri = retorno.uri;

      if(Counter == 1)
        props.modificaFoto1(uri);

      else if(Counter == 11)
        props.modificaFoto2(uri);

      else if(Counter == 111)
        props.modificaFoto3(uri);

      else if(Counter == 1111)
       props.modificaFoto4(uri);

      else if(Counter == 11111)
       props.modificaFoto5(uri);

      setImagem(
        [...Imagem,uri]
      )
    }


 }}



  return (
    <>
    <Header
    containerStyle={{ backgroundColor: '#8a2be2' }}
    leftComponent={{ icon: 'arrow-back', color: '#fff', onPress:() => Actions.navigation(), size: 35  }}
    centerComponent={{ icon:'add-to-photos', size: 35, color: '#fff', onPress:() => openImagePickerAsync()}}
    rightComponent={{ icon: 'camera-alt', color: '#fff',  onPress:() => snap() , size: 35 }}
    />




  <Camera style={{ flex: 1 }} type={type} ref={ref => { this.camera = ref; }}  >
        <View
          style={{
            flex: 1,
            backgroundColor: 'transparent',
            flexDirection: 'row',
          }}>

        </View>

    <View style={{width: 300, height: 100, flexDirection: 'row'}}>
    <TouchableOpacity  onPress={()=>  clickAlert(0)} style={styles.loadButton}>
    <Image source={{ uri: Imagem[0]  }} style={{ width: 60, height: 90}}  resizeMode="contain"/>
    </TouchableOpacity>     
    <TouchableOpacity  onPress={()=> clickAlert(1)} style={styles.loadButton}>
      <Image source={{ uri: Imagem[1] }} style={{ width: 60, height: 90}}  resizeMode="contain"/>
      </TouchableOpacity>
      <TouchableOpacity  onPress={()=> clickAlert(2)} style={styles.loadButton}>
      <Image source={{ uri: Imagem[2]  }} style={{ width: 60, height: 90}}  resizeMode="contain"/>
      </TouchableOpacity>
      <TouchableOpacity  onPress={()=>  clickAlert(3)} style={styles.loadButton}>
      <Image source={{ uri: Imagem[3]  }} style={{ width: 60, height: 90}}  resizeMode="contain"/>
      </TouchableOpacity>
      <TouchableOpacity  onPress={()=> clickAlert(4)} style={styles.loadButton}>
      <Image source={{ uri: Imagem[4]  }} style={{ width: 60, height: 90}}  resizeMode="contain"/>
      </TouchableOpacity>

      </View>
      </Camera>


    </>
  );
};

const mapStateToProps = state =>(
  {
    email: state.AutenticacaoReducer.emailLogin
  }

)

export default   connect(mapStateToProps, {modificaFoto1, modificaFoto2, modificaFoto3, modificaFoto4, modificaFoto5})(FormCadastroProblema);


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 305,
    height: 159,
    marginBottom: 20,
  },
  instructions: {
    color: '#888',
    fontSize: 18,
    marginHorizontal: 15,
    marginBottom: 10,
  },
  button: {
    backgroundColor: 'blue',
    padding: 20,
    borderRadius: 5,
  },
  loadButton: {
    width: 50,
    height: 50,
    borderRadius: 25,
    justifyContent: 'center',
    alignItems: 'center',
    marginLeft: 15,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  },
});
